# binary = ''

binary = '123456123456123456123456123456123456123456123456123456'
listy = []
for i in binary:
    listy.append(i)

def stripe(list, slice):
    blist = []
    for i in range(slice):
        for j in list[i::slice]:
            blist.append(j)
    return(blist)

print("".join(stripe(listy, 6)))